import { loginData } from "../../api/index";
export default {
  data: function data() {
    return {
      query: {
        login: "",
        ps: ""
      },
      param: {
        username: 'JDthd',
        password: ''
      },
      rules: {
        username: [{
          required: true,
          message: '请输入用户名',
          trigger: 'blur'
        }],
        password: [{
          required: true,
          message: '请输入密码',
          trigger: 'blur'
        }]
      }
    };
  },
  methods: {
    submitForm: function submitForm() {
      var _this = this;

      this.$refs.login.validate(function (valid) {
        if (valid) {
          var params = {
            login: _this.param.username,
            ps: _this.param.password
          };
          loginData(params).then(function (res) {
            console.log(res);

            if (res.code) {
              localStorage.setItem('ms_username', _this.param.username);
              localStorage.setItem('ms_ps', res.user.ps);
              var pp = res.user.path.split(';');
              localStorage.setItem('ms_paths', pp);

              _this.$message.success('登录成功');

              _this.$router.push(pp[0]);
            } else {
              _this.$message.error('请输入正确的账号和密码');

              console.log('error submit!!');
              return false;
            }
          });
        } else {
          _this.$message.error('请输入账号和密码');

          console.log('error submit!!');
          return false;
        }
      });
    }
  }
};
