import { orderListData } from "../../api/index";
import { orderEditData } from "../../api/index";
import { orderSearchData } from "../../api/index";
import { orderAddData } from "../../api/index";
export default {
  name: 'shopList',
  data: function data() {
    return {
      query: {
        pageIndex: 1,
        pageSize: 10
      },
      tableData: [],
      multipleSelection: [],
      delList: [],
      editVisible: false,
      addVisible: false,
      pageTotal: 0,
      editOrderform: {},
      addOrderform: {},
      multiShop: [],
      form: {},
      idx: -1,
      id: -1
    };
  },
  created: function created() {
    this.getData();
  },
  methods: {
    // 获取 easy-mock 的模拟数据
    getData: function getData() {
      var _this = this;

      orderListData(this.query).then(function (res) {
        console.log(res);
        _this.tableData = res.data;
        _this.pageTotal = res.pageTotal;
      });
    },
    // 触发搜索按钮
    handleSearch: function handleSearch() {
      var _this2 = this;

      this.$set(this.query, 'pageIndex', 1);
      orderSearchData(this.query).then(function (res) {
        console.log(res);
        _this2.tableData = res.data;
        _this2.pageTotal = res.pageTotal;
      });
    },
    // 执行操作
    handleExe: function handleExe(index, row) {
      var _this3 = this;

      // 二次确认执行
      this.$confirm('确定要执行吗？', '提示', {
        type: 'warning'
      }).then(function (res) {
        _this3.idx = index;
        orderExeData(row).then(function (res) {
          console.log(res);

          if (res == 1) {
            _this3.$message.success('执行成功');

            row.status = 2;
          }
        });
      }).catch(function () {});
    },
    // 多选操作
    handleSelectionChange: function handleSelectionChange(val) {
      this.multipleSelection = val;
    },
    delAllSelection: function delAllSelection() {
      var length = this.multipleSelection.length;
      var str = '';
      this.delList = this.delList.concat(this.multipleSelection);

      for (var i = 0; i < length; i++) {
        str += this.multipleSelection[i].name + ' ';
      }

      this.$message.error("\u5220\u9664\u4E86".concat(str));
      this.multipleSelection = [];
    },
    // 编辑操作
    handleEdit: function handleEdit(index, row) {
      this.idx = index;
      this.editOrderform = row;
      this.editVisible = true;
    },
    // 保存编辑
    saveEdit: function saveEdit() {
      var _this4 = this;

      orderEditData(this.editOrderform).then(function (res) {
        console.log(res);

        if (res == 1) {
          _this4.editVisible = false;

          _this4.$message.success("\u4FEE\u6539\u7B2C ".concat(_this4.idx + 1, " \u884C\u6210\u529F"));

          _this4.$set(_this4.tableData, _this4.idx, _this4.editOrderform);
        } else {
          _this4.$message.error('修改出错！');

          console.log('error edit Order!!');
          return false;
        }
      });
    },
    handleAdd: function handleAdd() {
      this.addVisible = true;
    },
    // 保存添加
    saveAdd: function saveAdd() {
      var _this5 = this;

      orderAddData(this.addOrderform).then(function (res) {
        console.log(res);

        if (res == 1) {
          _this5.addVisible = false;

          _this5.$message.success("\u589E\u52A0Order\u6210\u529F");

          _this5.getData();
        } else {
          _this5.$message.error('增加Order出错！');

          console.log('error add shop!!');
          return false;
        }
      });
    },
    // 分页导航
    handlePageChange: function handlePageChange(val) {
      var _this6 = this;

      this.$set(this.query, 'pageIndex', val);
      orderSearchData(this.query).then(function (res) {
        console.log(res);
        _this6.tableData = res.data;
        _this6.pageTotal = res.pageTotal;
      });
    },
    formatDate: function formatDate(row, column) {
      // 获取单元格数据
      var data = row[column.property];

      if (data == null) {
        return null;
      }

      var dt = new Date(data);
      return dt.getFullYear() + '-' + (dt.getMonth() + 1) + '-' + dt.getDate();
    },
    formatShopName: function formatShopName(row, column) {
      // 获取单元格数据
      var data = row[column.property];
      if (this.multiShop == null) return null;

      for (var i = 0; i < this.multiShop.length; i++) {
        if (data == this.multiShop[i].id) return this.multiShop[i].shopName;
      }
    }
  }
};
